package com.skyzer.server.main;

import java.util.Properties;
import java.util.concurrent.ExecutorService;
import java.util.concurrent.Executors;

import javax.mail.BodyPart;
import javax.mail.Message;
import javax.mail.Multipart;
import javax.mail.PasswordAuthentication;
import javax.mail.Session;
import javax.mail.Transport;
import javax.mail.internet.InternetAddress;
import javax.mail.internet.MimeBodyPart;
import javax.mail.internet.MimeMessage;
import javax.mail.internet.MimeMultipart;

public class Email {

	private Properties props;
	private Session session;
	private final String username = "skyzertms@gmail.com";
	private final String password = "Skynet123";
	private String bodyText = "";
    
	
	public Email() {
		props = new Properties();
        props.put("mail.smtp.auth", "true");
        props.put("mail.smtp.starttls.enable", "true");
        props.put("mail.smtp.host", "smtp.gmail.com");
        props.put("mail.smtp.port", "25");
        props.put("mail.smtp.ssl.protocols", "TLSv1.2");
        
        session = Session.getInstance(props,
                new javax.mail.Authenticator() {
                   protected PasswordAuthentication getPasswordAuthentication() {
                      return new PasswordAuthentication(username, password);
                   }
                });
	}
	
	public boolean sendSignUpAcknowledgement(String recipient) {
		try {
		 	
	           Message message = new MimeMessage(session);

	           message.setFrom(new InternetAddress(username, "Skyzer Technologies"));
	           message.setRecipients(Message.RecipientType.TO, InternetAddress.parse(recipient));
	           message.setSubject("Skyzer Guide");
	          
	           BodyPart messageBodyPart = new MimeBodyPart();
	           bodyText = this.acknowledgementEmailBody();
	           messageBodyPart.setContent(bodyText, "text/html");
	           Multipart multipart = new MimeMultipart();
	           multipart.addBodyPart(messageBodyPart);
	          
	           message.setContent(multipart);
	           
	           // Email Synchronization 
	           ExecutorService emailExecutor = Executors.newCachedThreadPool();
	           emailExecutor.execute(new Runnable() {
	               @Override
	               public void run() {
	                   try {
	                	   Transport.send(message);
	                   } catch (Exception e) {
	                	   e.printStackTrace();
	                   }
	               }
	           });
	           
	           return true;
	           
	        } catch (Exception e) {
	        	e.printStackTrace();
	        	return false;
	        }
	}
	
	private String acknowledgementEmailBody() {
		return "<p style=\"text-align: left;\"><br></p>\r\n" + 
				"<p style=\"text-align: left;\"><strong><span style=\"font-family: Arial, Helvetica, sans-serif;\">Thank you for joining the Skyzer Guide!</span></strong><br><span style=\"font-family: Arial, Helvetica, sans-serif;\"><br>Skyzer Guide provides you with a better experience with EFTPOS machine solutions...</span><br><span style=\"font-family: Arial, Helvetica, sans-serif;\"><br>We&apos;ll send you the activation email to get started soon!<br><br>Have questions? We&apos;d love to help! Just hit reply :)<br><br>Our Best,</span><br><span style=\"font-family: Arial, Helvetica, sans-serif;\"><strong>Skyzer Technologies</strong></span></p>\r\n" + 
				"<p><span style=\"font-family: Arial, Helvetica, sans-serif;\"><strong><img src=\"data:image/png;base64,\"></strong></span></p>\r\n" + 
				"<p style=\"text-align: center;\"><span style=\"font-family: Arial, Helvetica, sans-serif;\"><span style=\"font-size: 12px;\">Made by</span><strong><span style=\"font-size: 12px;\">&nbsp;</span></strong></span><a href=\"https://www.skyzer.co.nz/\" rel=\"noopener noreferrer\" target=\"_blank\"><span style=\"font-family: Arial, Helvetica, sans-serif;\"><strong><span style=\"font-size: 12px;\">Skyzer Technologies</span></strong></span></a></p>";
	}
}